const jobFormHandler=async e=>{e.preventDefault();let t=[];const o=document.querySelector("#job-title-create").value.trim(),r=document.querySelector("#job-description-create").value.trim(),a=document.querySelector("#qualifications-create").value.trim(),c=document.querySelector("#location-create").value.trim(),i=document.querySelector("#website-create").value.trim();document.getElementsByName("developer-type").forEach((e=>{e.checked&&t.push(e.value)}));let n=t[0];if(o&&r&&a&&c&&i&&n){const e=await fetch(`/api/jobs/${n}`,{method:"POST",body:JSON.stringify({name:o,description:r,qualifications:a,location:c,website:i,category:n}),headers:{"Content-Type":"application/json"}});e.ok?document.location.replace("/profile/employer/jobs"):alert(e.statusText)}};document.querySelector(".jobCreateForm").addEventListener("submit",jobFormHandler);