const signupFormHandler=async e=>{e.preventDefault();let t=[];const o=document.querySelector("#name-signup").value.trim(),r=document.querySelector("#email-signup").value.trim(),n=document.querySelector("#password-signup").value.trim(),u=document.querySelector("#location-signup").value.trim(),i=document.querySelector("#website-signup").value.trim(),a=document.querySelector("#bio-signup").value.trim(),s=document.querySelector("#name-signup").getAttribute("data-role");if(document.getElementsByName("developer-type").forEach((e=>{e.checked&&t.push(e.value)})),t=JSON.stringify(t),o&&r&&n&&u&&a&&s){const e=await fetch("/api/users",{method:"POST",body:JSON.stringify({name:o,email:r,password:n,location:u,website:i,bio:a,role:s,categories:t}),headers:{"Content-Type":"application/json"}});e.ok?document.location.replace("/"):alert(e.statusText)}};document.querySelector(".signup-form").addEventListener("submit",signupFormHandler);